import React, { useState, useEffect } from "react";
import "./App.css";


interface TableProps {
  name: string;
  email: string;
  firstName: string;
  lastName: string;
}

const Table = ({ name, email, firstName, lastName }: TableProps) => {
  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>First Name</th>
          <th>Last Name</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>{name}</td>
          <td>{email}</td>
          <td>{firstName}</td>
          <td>{lastName}</td>
        </tr>
      </tbody>
    </table>
  );
};

const LoginPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [updated, setUpdated] = useState(false);
  const [initialValues, setInitialValues] = useState({
    name: "",
    email: "",
    firstName: "",
    lastName: "",
  });

  useEffect(() => {
    const savedName = localStorage.getItem("name");
    const savedEmail = localStorage.getItem("email");
    const savedFirstName = localStorage.getItem("firstName");
    const savedLastName = localStorage.getItem("lastName");

    if (savedName) {
      setName(savedName);
      setInitialValues((prevState) => ({
        ...prevState,
        name: savedName,
      }));
    }

    if (savedEmail) {
      setEmail(savedEmail);
      setInitialValues((prevState) => ({
        ...prevState,
        email: savedEmail,
      }));
    }

    if (savedFirstName) {
      setFirstName(savedFirstName);
      setInitialValues((prevState) => ({
        ...prevState,
        firstName: savedFirstName,
      }));
    }

    if (savedLastName) {
      setLastName(savedLastName);
      setInitialValues((prevState) => ({
        ...prevState,
        lastName: savedLastName,
      }));
    }
  }, []);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    localStorage.setItem("name", name);
    localStorage.setItem("email", email);
    localStorage.setItem("firstName", firstName);
    localStorage.setItem("lastName", lastName);
    setUpdated(true);
    setInitialValues({
      name: "",
      email: "",
      firstName: "",
      lastName: "",
    });
  };

  const handleNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setName(event.target.value);
    setUpdated(false);
  };


  const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(event.target.value);
    setUpdated(false);
  };

  const handleFirstNameChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setFirstName(event.target.value);
    setUpdated(false);
  };
  const handleUpdate = () => {
    setUpdated(true);
  };

  const handleLastNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setLastName(event.target.value);
    setUpdated(false);
  };

  return (
    <div>
      <form className="form-design" onSubmit={handleSubmit}>
        <div className="name-field">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            className="name-input"
            id="name"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={handleEmailChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={handleFirstNameChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={handleLastNameChange}
          />
        </div>
        <button className="submit-btn" type="submit">
          Submit
        </button>
        <button className="submit-btn" type="button" onClick={handleUpdate}>
          Update
        </button>
      </form>
      {updated && (
        <Table
          name={name}
          email={email}
          firstName={firstName}
          lastName={lastName}
        />
      )}
    </div>
    //  {updated && <p>The information has been updated.</p>}
  );
};

function App() {
  return <LoginPage />;
}

export default App;
