import React, { useState, useEffect } from "react";
import "./App.css";

const LoginPage = () => {
  const [inputFormState, setInputFormState] = useState({
    name: "",
    email: "",
    firstName: "",
    lastName: "",
  });
  const [updated, setUpdated] = useState(false);

  useEffect(() => {
    const savedForm = JSON.parse(localStorage.getItem("inputForm") || "{}");
    setInputFormState(savedForm);
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (updated) {
      localStorage.setItem("inputForm", JSON.stringify(inputFormState));
    }
    setUpdated(false);
  };

  const handleUpdate = () => {
    setUpdated(true);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInputFormState((prev) => ({
      ...prev,
      [name]: value,
    }));
    setUpdated(false);
  };

  return (
    <form className="form-design" onSubmit={handleSubmit}>
      <div className="name-field">
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          className="name-input"
          id="name"
          name="name"
          value={inputFormState.name}
          onChange={handleInputChange}
        />
      </div>
      <div className="name-field">
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={inputFormState.email}
          onChange={handleInputChange}
        />
      </div>
      <div className="name-field">
        <label htmlFor="firstName">First Name:</label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          value={inputFormState.firstName}
          onChange={handleInputChange}
        />
      </div>
      <div className="name-field">
        <label htmlFor="lastName">Last Name:</label>
        <input
          type="text"
          id="lastName"
          name="lastName"
          value={inputFormState.lastName}
          onChange={handleInputChange}
        />
      </div>
      <button className="submit-btn" type="submit">
        Submit
      </button>
      <button className="submit-btn" type="button" onClick={handleUpdate}>
        Update
      </button>
      {updated && <p>The information has been updated.</p>}
      {/*  conditional rendering */}
    </form>
  );
};

function App() {
  return <LoginPage />;
}

export default App;
