import React, { useState, useEffect } from "react";
import "./App.css";



interface TableProps {
  data: {
    name: string;
    email: string;
    firstName: string;
    lastName: string;
  }[];
}

const Table = ({ data }: TableProps) => {
  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>First Name</th>
          <th>Last Name</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr key={index}>
            <td>{row.name}</td>
            <td>{row.email}</td>
            <td>{row.firstName}</td>
            <td>{row.lastName}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

const LoginPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [tableData, setTableData] = useState<
    {
      name: string;
      email: string;
      firstName: string;
      lastName: string;
    }[]
  >([]);
  const [updated, setUpdated] = useState(false);

  useEffect(() => {
    const savedTableData = localStorage.getItem("tableData");

    if (savedTableData) {
      setTableData(JSON.parse(savedTableData));
    }
  }, []);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const newRow = {
      name,
      email,
      firstName,
      lastName,
    };
    setTableData([...tableData, newRow]);
    localStorage.setItem("tableData", JSON.stringify([...tableData, newRow]));
    setUpdated(true);
    setName("");
    setEmail("");
    setFirstName("");
    setLastName("");
  };

  const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(event.target.value);
    setUpdated(false);
  };

  const handleFirstNameChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setFirstName(event.target.value);
    setUpdated(false);
  };
  const handleUpdate = () => {
    setUpdated(true);
  };

  const handleReset = () => {
    setName(initialValues.name);
    setEmail(initialValues.email);
    setFirstName(initialValues.firstName);
    setLastName(initialValues.lastName);
    setUpdated(false);
  };

  const handleLastNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setLastName(event.target.value);
    setUpdated(false);
  };

  return (
    <div>
      <form className="form-design" onSubmit={handleSubmit}>
        <div className="name-field">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            className="name-input"
            id="name"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={handleEmailChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={handleFirstNameChange}
          />
        </div>
        <div className="name-field">
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={handleLastNameChange}
          />
        </div>
        <button className="submit-btn" type="submit">
          Submit
        </button>
        <button className="submit-btn" type="button" onClick={handleUpdate}>
          Update
        </button>
      </form>
      {updated && (
        <Table
          name={name}
          email={email}
          firstName={firstName}
          lastName={lastName}
        />
      )}
    </div>
    //  {updated && <p>The information has been updated.</p>}
  );
};

function App() {
  return <LoginPage />;
}

export default App;
