import { useEffect, useState } from "react";
import { useLocalStorage } from "./components/useLocalStorage";

interface InputForm {
  name: string;
  website: string;
  contact: Contact;
}

interface Contact {
  cell: string;
  email: string;
}

const initialForm: InputForm = {
  name: "",
  website: "",
  contact: {
    cell: "",
    email: "",
  },
};

const App = () => {
  const [savedForm, setSavedForm, clearLocalStorage] =
    useLocalStorage<InputForm>("inputForm", initialForm);
  const [inputFormState, setInputFormState] = useState<InputForm>(savedForm);

  const handleFormChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setInputFormState((prev) => {
      const newForm: InputForm = { ...prev };
      if (name === "name") {
        newForm.name = value;
      } else if (name === "website") {
        newForm.website = value;
      } else if (name === "cell") {
        newForm.contact.cell = value;
      } else if (name === "email") {
        newForm.contact.email = value;
      }
      return newForm;
    });
  };

  useEffect(() => {
    setSavedForm(inputFormState);
  }, [inputFormState, setSavedForm]);

  return (
    <div>
      <div>
        <h3>React js Local Storage</h3>
      </div>
      <div>
        Name:
        <input
          name="name"
          value={inputFormState.name}
          onChange={(e) => handleFormChange(e)}
        />
      </div>
      <div>
        Website:
        <input
          name="website"
          value={inputFormState.website}
          onChange={(e) => handleFormChange(e)}
        />
      </div>
      <div>
        Cell:
        <input
          name="cell"
          value={inputFormState.contact.cell}
          onChange={(e) => handleFormChange(e)}
        />
      </div>
      <div>
        Email:
        <input
          name="email"
          value={inputFormState.contact.email}
          onChange={(e) => handleFormChange(e)}
        />
      </div>
      <div>
        <button onClick={() => clearLocalStorage()}>Clear Cache</button>
      </div>
    </div>
  );
};

export default App;
